// src/context/GameContext.js
import { reactive, provide, inject } from 'vue';

const GameStateSymbol = Symbol();

export function provideGameState() {
  const gameState = reactive({
    players: [],
    currentPlayer: null,
    monsters: [],
  });

  const setGameState = (newState) => {
    Object.assign(gameState, newState);
  };

  provide(GameStateSymbol, { gameState, setGameState });
}

export function useGameState() {
  const context = inject(GameStateSymbol);
  if (!context) {
    throw new Error('useGameState must be used within a provideGameState');
  }
  return context;
}
