// Code for the JoinLobby component
// 

<template>
    <div class="join-lobby">
      <h1>Join Lobby</h1>
      <input v-model="lobbyId" placeholder="Enter Lobby ID" />
      <button @click="joinLobby">Join Lobby</button>
    </div>
  </template>
  
  <script>
  import { useRouter } from 'vue-router';
  import { joinLobby } from '../services/firebaseService';
  
  export default {
    name: 'JoinLobby',
    setup() {
      const router = useRouter();
      // eslint-disable-next-line no-undef
      const lobbyId = ref('');
  
      const handleJoinLobby = async () => {
        try {
          await joinLobby(lobbyId.value);
          router.push(`/lobby/${lobbyId.value}`);
        } catch (error) {
          console.error('Error joining lobby:', error);
          alert('Failed to join lobby: ' + error.message);
        }
      };
  
      return { lobbyId, joinLobby: handleJoinLobby };
    }
  };
  </script>
  
  <style scoped>
  .join-lobby {
    text-align: center;
    margin-top: 50px;
  }
  .join-lobby input {
    margin: 10px;
    padding: 10px;
    font-size: 1em;
  }
  .join-lobby button {
    margin: 10px;
    padding: 10px 20px;
    font-size: 1em;
    cursor: pointer;
  }
  </style>
