// Code for the JoinLobby component
// This component is used to join an existing lobby by entering the lobby ID

<template>
  <div class="join-lobby">
    <h1>Join a Lobby</h1>
    <form @submit.prevent="joinLobby">
      <input v-model="lobbyId" placeholder="Enter Lobby ID" required>
      <button type="submit">Join Lobby</button>
    </form>
  </div>
</template>

<script>
import { getAuth } from 'firebase/auth';
import { useRouter } from 'vue-router';
import { doc, updateDoc, arrayUnion, getDoc } from 'firebase/firestore';
import { db } from '../services/firebaseConfig';

export default {
  name: 'JoinLobby',
  data() {
    return {
      lobbyId: ''
    };
  },
  setup() {
    const router = useRouter();
    const auth = getAuth();

    const joinLobby = async () => {
      try {
        const user = auth.currentUser;
        if (!user) {
          alert('User not authenticated');
          router.push('/login');
          return;
        }

        const lobbyRef = doc(db, 'lobbies', this.lobbyId);
        const lobbySnap = await getDoc(lobbyRef);

        if (lobbySnap.exists()) {
          await updateDoc(lobbyRef, {
            players: arrayUnion(user.uid)
          });
          router.push(`/lobby/${this.lobbyId}`);
        } else {
          alert('Lobby not found');
        }
      } catch (error) {
        console.error('Error joining lobby:', error);
        alert('Failed to join lobby: ' + error.message);
      }
    };

    return { joinLobby };
  }
};
</script>

<style scoped>
.join-lobby {
  text-align: center;
  margin-top: 50px;
}
.join-lobby h1 {
  font-size: 2.5em;
  margin-bottom: 20px;
}
.join-lobby form {
  display: flex;
  flex-direction: column;
  align-items: center;
}
.join-lobby input {
  margin-bottom: 10px;
  padding: 10px;
  font-size: 1em;
  width: 300px;
}
.join-lobby button {
  padding: 10px 20px;
  font-size: 1em;
  cursor: pointer;
}
</style>
