// This component is the main menu for the multiplayer options. 
// It has two buttons, one to create a lobby and one to join a lobby. 
// The buttons are implemented using Vue Router's router-link component, which allows us to navigate to different routes in our application. The buttons are styled using CSS to make them visually appealing.

<template>
    <div class="multiplayer-options">
      <h1>Multiplayer Options</h1>
      <router-link to="/create-lobby">
        <button>Create Lobby</button>
      </router-link>
      <router-link to="/join-lobby">
        <button>Join Lobby</button>
      </router-link>
    </div>
  </template>
  
  <script>
  export default {
    name: 'MultiplayerOptions',
  };
  </script>
  
  <style scoped>
  .multiplayer-options {
    text-align: center;
    margin-top: 50px;
  }
  .multiplayer-options h1 {
    font-size: 2.5em;
    margin-bottom: 20px;
  }
  .multiplayer-options button {
    margin: 10px;
    padding: 10px 20px;
    font-size: 1em;
    cursor: pointer;
  }
  </style>
